section.code:
/**
 * Example 1.2
 *
 * (c) Stephen Fryatt, 2015
 */

#include "oslib/wimp.h"

int main()
{
	osbool		quit = FALSE;
	wimp_block	block;
	wimp_event_no	reason;

	wimp_initialise(wimp_VERSION_RO3, "Wimp Task", NULL, NULL);

	while (!quit) {
		reason = wimp_poll((wimp_poll_flags) 0x1a73, &block, NULL);

		switch (reason) {
		case wimp_USER_MESSAGE:
		case wimp_USER_MESSAGE_RECORDED:
			if (block.message.action == message_QUIT)
				quit = TRUE;
		}
	}

	wimp_close_down(0);

	return 0;
}

/**
 * Wimp Poll loop.
 */

static void main_poll_loop(void)
{
	wimp_block	blk;
	wimp_event_no	reason;
	int		pollword;

	while (!main_quit_flag) {
		reason = wimp_poll(wimp_MASK_NULL, &blk, &pollword);

		/* Events are passed to Event Lib first; only if this fails to
		 * handle them do they get passed on to the switch statement.
		 */

		if (!event_process_event(reason, &blk, pollword)) {
			switch (reason) {

			case wimp_OPEN_WINDOW_REQUEST:
				wimp_open_window(&(blk.open));
				break;

			case wimp_CLOSE_WINDOW_REQUEST:
				wimp_close_window(blk.close.w);
				break;
			}
		}
	}
}
